
public class 문제9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=10, num2=10;
	    System.out.printf("%d \n", ++num1); // 11
	    System.out.printf("%d \n", num1); // 11
	    System.out.printf("%d \n", num2++); // 10
	    System.out.printf("%d \n", num2);// 11

	}

}
